<?php$id = $_REQUEST['id'];if(empty($id)){	echo "invalid";	exit;}$url = "http://www.sitewarranty.com/cgi-bin/swc/mreg.cgi?useridexists=".$id;if(function_exists('file_get_contents') && file_get_contents(__FILE__)){	$response = file_get_contents($url);	if($response == 1){		echo "valid";	}	else{		echo "invalid";	}}elseif(function_exists('curl_version') && function_exists('curl_init') && function_exists('curl_exec')){	$handle = curl_init($url);	curl_setopt($handle,  CURLOPT_RETURNTRANSFER, TRUE);	$response = curl_exec($handle);	if($response == 1){		echo "valid";	}	else{		echo "invalid";	}}else{	echo 'err01';}?>